package main

import (
	"bytes"
	"encoding/base64"
	"encoding/json"
	"fmt"
	"github.com/stellar/go/xdr"
	"log"
)

func main() {
	//helloworld()
	//statistics()
	//claimables()
	prettyPrintLcm()
}

func prettyPrintLcm() {
	lcmEncoded := ``

	lcmBytes, err := base64.StdEncoding.DecodeString(lcmEncoded)
	if err != nil {
		log.Fatalf("Error decoding base64: %v", err)
	}

	reader := bytes.NewReader(lcmBytes)
	var lcm xdr.LedgerCloseMeta
	_, err = xdr.Unmarshal(reader, &lcm)
	if err != nil {
		log.Fatalf("Error unmarshalling XDR: %v", err)
	}

	lcmJson, err := json.MarshalIndent(lcm, "", "    ")
	if err != nil {
		log.Fatalf("Error converting to JSON: %v", err)
	}
	fmt.Println("----------")
	fmt.Println(string(lcmJson))
	fmt.Println("----------")

}
